contracts/subscription.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.4;

// import "hardhat/console.sol";
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";

contract SubscriptionHandler is AccessControlEnumerable {
    struct Subscription {
        uint8 level;
        uint256 endDate;
    }

    bytes32 public constant SUBS_ROLE = keccak256("SUBS_ROLE");

    mapping(address => Subscription) subscriptions;

    event SetSubscription(
        address indexed userAddr,
        uint8 userLevel,
        uint256 endDate
    );

    constructor(address creator) {
        _setupRole(SUBS_ROLE, msg.sender);
        if (msg.sender != creator) {
            _setupRole(SUBS_ROLE, creator);
        }
    }

    // userPeriod is in seconds
    function setSubscription(
        address userAddr,
        uint8 userLevel,
        uint256 userPeriod
    ) public onlyRole(SUBS_ROLE) {
        uint256 endDate = block.timestamp + userPeriod;
        subscriptions[userAddr].level = userLevel;
        subscriptions[userAddr].endDate = endDate;
        emit SetSubscription(userAddr, userLevel, endDate);
    }

    function getSubscription(address userAddr)
        public
        view
        returns (Subscription memory)
    {
        return subscriptions[userAddr];
    }

    function checkSubscription(address userAddr, uint8 contractLevel)
        public
        view
        returns (bool)
    {
        bool valid = false;
        if (
            subscriptions[userAddr].level >= contractLevel &&
            subscriptions[userAddr].endDate > block.timestamp
        ) {
            valid = true;
        }
        return valid;
    }
}

contracts/mainfactory.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.4;

import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "./factory_contracts/tokenfactory.sol";
import "./mock_contracts/IAccountRules.sol";
import "./ISubscription.sol";
import "./IFactoryRecords.sol";

contract MainFactory is AccessControlEnumerable {
    event Log(string message);
    event LogBytes(bytes data);

    bytes32 public constant FACTORY_ROLE = keccak256("FACTORY_ROLE");

    IAccountRules public accountRules;
    IFactoryRecords public factoryRecords;
    ISubscriptionHandler public subscriptionService;

    uint8 public TokenFactoryLevel = 0;

    modifier requireAccountRules() {
        require(address(accountRules) != address(0), "AccountRules not set !");
        _;
    }

    modifier requireFatcoryRecords() {
        require(
            address(factoryRecords) != address(0),
            "Factory records not set !"
        );
        _;
    }

    modifier requireSubscription(uint8 level) {
        require(
            address(subscriptionService) != address(0),
            "Subscription service not set !"
        );
        require(checkSubscription(level), "Subscription not valid, sorry!");
        _;
    }

    constructor() {
        _setupRole(FACTORY_ROLE, msg.sender);
    }

    function setAccountRules(address _accountRules)
        public
        onlyRole(FACTORY_ROLE)
    {
        accountRules = IAccountRules(_accountRules);
    }

    function setFactoryRecords(address _factoryRecords)
        public
        onlyRole(FACTORY_ROLE)
    {
        factoryRecords = IFactoryRecords(_factoryRecords);
    }

    function setSubscriptionService(address _subscriptionService)
        public
        onlyRole(FACTORY_ROLE)
    {
        subscriptionService = ISubscriptionHandler(_subscriptionService);
    }

    function checkSubscription(uint8 level)
        private
        view
        requireFatcoryRecords
        returns (bool)
    {
        if (level > 0) {
            return subscriptionService.checkSubscription(msg.sender, level);
        }
        return true;
    }

    function deployToken(string memory name, string memory symbol) public {
        TokenFactory tokenFactory = new TokenFactory(
            msg.sender,
            name,
            symbol,
            new address[](0)
        );

        bytes32 _factoryName = tokenFactory.getContractName();
        uint256 _version = tokenFactory.getContractVersion();

        AddContractRecord(
            address(tokenFactory),
            msg.sender,
            _factoryName,
            _version
        );

        AddContractPerm(address(tokenFactory));
    }

    function AddContractPerm(address deployContract)
        private
        requireAccountRules
    {
        try accountRules.addAccount(deployContract) returns (bool res) {
            if (res) {
                emit Log("AccountRules Added successfully !");
            }
        } catch Error(string memory reason) {
            emit Log(reason);
        } catch (bytes memory lowLevelData) {
            emit LogBytes(lowLevelData);
        }
    }

    function AddContractRecord(
        address deplContract,
        address owner,
        bytes32 _factoryName,
        uint256 _version
    ) private requireSubscription(TokenFactoryLevel) {
        try
            factoryRecords.addDepl(deplContract, owner, _factoryName, _version)
        returns (bool res) {
            if (res) {
                emit Log("Contract Added successfully to factoryRecords !");
            }
        } catch Error(string memory reason) {
            emit Log(reason);
        } catch (bytes memory lowLevelData) {
            emit LogBytes(lowLevelData);
        }
    }
}

contracts/factory_contracts/tokenfactory.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.4;

// import "hardhat/console.sol";
// import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC777/ERC777.sol";
import "./privacy.sol";

contract TokenFactory is ERC777, Privacy {
    event ERC777Deployed(
        address indexed tokenAddr,
        address indexed creatorAddr,
        string name,
        string symbol,
        address[] defaultOperators
    );

    bytes32 public constant CONTRACT_NAME = keccak256("TOKEN_FACTORY");
    uint256 public constant CONTRACT_VERSION = 1000;

    constructor(
        address creator,
        string memory name,
        string memory symbol,
        address[] memory defaultOperators
    ) ERC777(name, symbol, defaultOperators) {
        owner = creator;
        usersAllowList.push(owner);
        indexOf[owner] = usersAllowList.length;

        emit ERC777Deployed(
            address(this),
            creator,
            name,
            symbol,
            defaultOperators
        );
    }

    function mint(uint256 amount) public onlyOwner {
        _mint(msg.sender, amount, "", "");
    }

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        super._beforeTokenTransfer(operator, from, to, amount);
        require(isUserAllowed(msg.sender), "Sorry, this contract is private");
    }

    function getContractName() public pure returns (bytes32) {
        return CONTRACT_NAME;
    }

    function getContractVersion() public pure returns (uint256) {
        return CONTRACT_VERSION;
    }
}

